import React, { useState } from 'react';
import { v1 } from 'uuid';
import './App.css';
import { ToDoList } from './ToDoList';



export type FilterType = 'All' | 'Active' | 'Completed'


export type ToDoListsType = {
    title: string,
    id: string,
    filter: FilterType
}


function App() {


    let toDoListId1 = v1()
    let toDoListId2 = v1()



    let [toDoLists, setToDoList] = useState<Array<ToDoListsType>>([
        {
            title: 'what to learn',
            id: toDoListId1,
            filter: 'Completed'
        },
        {
            title: 'what to buy',
            id: toDoListId2,
            filter: 'Active'
        },
        {
            title: 'what i want',
            id: v1(),
            filter: 'Completed'
        },
    ])




    let [tasksO, setTasks] = useState({
        [toDoListId1]: [
            {
                task: 'js',
                id: v1(),
                description: 'something',
                checked: true,
            },
            {
                task: 'sccs',
                id: v1(),
                description: 'something',
                checked: true,
            },
            {
                task: 'html',
                id: v1(),
                description: 'something',
                checked: false,
            },
        ],
        //когда оборачиваем значение в квадратные скобки - значение само по себе становится ключом, а не тудулист2 становится ключом
        [toDoListId2]: [
            {
                task: 'books',
                id: v1(),
                description: 'something',
                checked: true,
            },
            {
                task: 'magazines',
                id: v1(),
                description: 'something',
                checked: true,
            },

        ],
    }
    )

    function changeCheckBox(id: string, checked: boolean) {

        let task = tasks.find(t => t.id === id)
        if (task) {
            task.checked = checked;
        }
        //создаем видимость того, что здесь лежит что-то новое
        // let copy=[...tasks]
        // setTasks(copy)

        //сокращенный вариант
        setTasks([...tasks])

    }

    let [filter, setFilter] = useState<FilterType>('Active')



    function addTask(newtext: string) {
        if (newtext.trim() !== '') {
            let newTask = {
                task: newtext,
                id: v1(),
                description: '',
                checked: false,
            }
            setTasks([newTask, ...tasks])
        }

    }

    function changeFilter(value: FilterType, toDoListId: string) {
        let toDoList = toDoLists.find((el) => el.id === toDoListId)
        if (toDoList) {
            toDoList.filter = value;
            setToDoList([...toDoLists])
        }
        setFilter(value)
    }

    let tasksForFilter = tasks





    function deleteTask(id: string, toDoListId: string) {
        let tasks
        let filtredTasks = tasks.filter((el) => { return el.id !== id })
        setTasks(filtredTasks)


    }


    return (
        <div className="App">

            {
                toDoLists.map((el: any) => {

                    let tasksForToDoList = tasks[el.id]


                    if (filter === 'Completed') {
                        //без ретурна!!! присваеваем фильтр
                        tasksForFilter = tasksForToDoList.filter(
                            el => el.checked === true
                        )
                    }


                    if (filter === 'Active') {
                        tasksForFilter = tasksForToDoList.filter(
                            el => el.checked === false
                        )
                    }


                    return <ToDoList
                        //не забудь добавить кей в мап
                        key={el.id}
                        id={el.id}
                        title={el.title}
                        tasks={tasksForFilter}
                        deleteTask={deleteTask}
                        changeFilter={changeFilter}
                        addTask={addTask}
                        changeCheckBox={changeCheckBox}
                        filter={el.filter} />
                })
            }

        </div>
    );
}


export default App;
