import React, { useState } from 'react';
import { FilterType } from './App';


export type TasksType={
    task: string,
    id:string,
    description:string,
    checked:boolean,
}

export type ToDoListType = {
    title: string,
    tasks: Array<TasksType>,
    deleteTask:(id:string)=>any,
    changeFilter:(value:FilterType)=>void
    addTask:(newtext:string)=>any
}


export function ToDoList({ title, tasks,deleteTask,changeFilter,addTask }: ToDoListType) {

    let mapFunction = tasks.map((el:any) => { return <li key={el.id}><input type="checkbox" checked={el.checked} /> <span>{el.task}
     <button onClick={()=>deleteTask(el.id)}>x</button></span></li> })

     let [value,setValue]=useState('')

     const onChabgeHandlerElement=(e:ChangeEvent<HTMLInputElement>)=>{setValue(e.currentTarget.value)}


    return (
        <div className="App">
            <div>
                <h3>{title}</h3>
                <div>
                    <input value={value} onChange={onChabgeHandlerElement
                    } />
                    <button onClick={()=>{addTask(value);setValue('');}}>+</button>
                </div>
                <ul>
                    {mapFunction}
            
                </ul>
                <div>
                    <button onClick={()=>changeFilter('All')
                    }>All</button>
                    <button onClick={()=>changeFilter('Active')}>Active</button>
                    <button onClick={()=>changeFilter('Completed')}>Completed</button>
                </div>
            </div>
        </div>
    );
}
