import React, { useState } from 'react';
import { v1 } from 'uuid';
import './App.css';
import { ToDoList } from './ToDoList';



export type FilterType = 'All' | 'Active' | 'Completed'


export type ToDoListsType = {
    title: string,
    id: string,
    filter: FilterType
}


function App() {


    let [tasks, setTasks] = useState([
        // {
        //     task: 'js',
        //     id: v1(),
        //     description: 'something',
        //     checked: true,
        // },
        // {
        //     task: 'sccs',
        //     id: v1(),
        //     description: 'something',
        //     checked: true,
        // },
        // {
        //     task: 'html',
        //     id: v1(),
        //     description: 'something',
        //     checked: false,
        // },
    ])




    function changeCheckBox(id: string, checked: boolean) {

        let task = tasks.find(t => t.id === id)
        if (task) {
            task.checked = checked;
        }
        //создаем видимость того, что здесь лежит что-то новое
        // let copy=[...tasks]
        // setTasks(copy)

        //сокращенный вариант
        setTasks([...tasks])

    }

    let [filter, setFilter] = useState<FilterType>('Active')



    function addTask(newtext: string) {
        if (newtext.trim() !== '') {
            let newTask = {
                task: newtext,
                id: v1(),
                description: '',
                checked: false,
            }
            setTasks([newTask, ...tasks])
        }

    }

    function changeFilter(value: FilterType, toDoListId: string) {
        let toDoList=toDoLists.find((el)=>el.id===toDoListId)
        if(toDoList){
            toDoList.filter=value;
            setToDoList([...toDoLists]) 
        }
        setFilter(value)
    }

    let tasksForFilter = tasks



    if (filter === 'Completed') {
        //без ретурна!!! присваеваем фильтр
        tasksForFilter = tasks.filter(
            el => el.checked === true
        )
    }


    if (filter === 'Active') {
        tasksForFilter = tasks.filter(
            el => el.checked === false
        )
    }



    function deleteTask(id: string) {

        let filtredTasks = tasks.filter((el) => { return el.id !== id })
        setTasks(filtredTasks)


    }


let toDoListId1=v1()
let toDoListId2=v1()

    let [toDoLists, setToDoList]=useState<Array<ToDoListsType>>([
        {
            title: 'what to learn',
            id: toDoListId1,
            filter: 'Completed'
        },
        {
            title: 'what to buy',
            id: toDoListId2,
            filter: 'Active'
        },
        {
            title: 'what i want',
            id: v1(),
            filter: 'Completed'
        },
    ])



    let [allTask,setAllTask]=useState({
        toDoListId1: [
            {
            task: 'js',
            id: v1(),
            description: 'something',
            checked: true,
        },
        {
            task: 'sccs',
            id: v1(),
            description: 'something',
            checked: true,
        },
        {
            task: 'html',
            id: v1(),
            description: 'something',
            checked: false,
        },
    ],
    toDoListId2: [
        {
        task: 'books',
        id: v1(),
        description: 'something',
        checked: true,
    },
    {
        task: 'sccs',
        id: v1(),
        description: 'something',
        checked: true,
    },
    {
        task: 'html',
        id: v1(),
        description: 'something',
        checked: false,
    },
],
}
)
    return (
        <div className="App">

            {
                toDoLists.map((el: any) => {
                    return <ToDoList
                        //не забудь добавить кей в мап
                        key={el.id}
                        id={el.id}
                        title={el.title}
                        tasks={tasksForFilter}
                        deleteTask={deleteTask}
                        changeFilter={changeFilter}
                        addTask={addTask}
                        changeCheckBox={changeCheckBox}
                        filter={el.filter} />
                })
            }

        </div>
    );
}


export default App;
