import React, { useState } from 'react';
import { v1 } from 'uuid';
import './App.css';
import { ToDoList } from './ToDoList';

//первое занятие- разбираемся как прокидывать пропсы и использовать типы , как создавать компоненты в других файлах

//занятие второе - учимся дебажить, писать мап, onclick,
//когда хотим что-то поменять в интерфейсе - думаем про то как изменить данные

//учимся удалять таски - пишем функцию, но чтобы удалить что-то нужны входные данные - айдишка, вызываем эту функцию в онклике , передаем туда айди, присваеваем старому массиву новый отфлитрованный внутри функции по таким параметрам - верни мне все кроме той, что пришла в параметрах
//присваеваем стейту таски сеттаск и юзстейт
//в функции говорим что функция сеттаск изменяет стейт при помощи фильтред таск

//учимся фильтровать таски в кнопках 
//нам нужен фильтр и сетфильтр с юзстейтом, которые будет читать кнопки фильтрации
//потом пишем условия иф ( фильтре будет актив ) верни нам в таскс фо фильтр только отфильтрованные 
// передаем таскс фор фильтр в пропсы таскс
//теперь нам надо чтобы считывались кнопки - пишим функцию чейнджфильтр, которая будет вызывать сетфильтр и типы для параметров этой функции 

//третье занятие -
//используй кей для родительского элмемента в мап с параметром айди

//добавляем таску в массив - пишем функцию, которая будет клепать похожие данные - элементы массива 
// потом будет вызывать сеттаскс и новую таску setTasks([newTask,...tasks])
//теперь вызываем в онклике на нужной кнопке 

//считываем с инпута валью при помощи ончейнж 
//пишем локальный юзстейт для валью 
//   let [value,setValue]=useState('')
//сам валью равен валью
// ОН ЧЕЙНДЖ (e)=>setValue(e.currentTarget.value)  - говорим эвент карент таргет и валью, эта штука будет считывать каждый символ, 

//задаем пустую строку в инпуте 
// <button onClick={()=>{addTask(value);setValue('');}}>+</button>
//пишем после эд таск сетвалью, которое меняет валью и задаем в параметрах пустую строку

//onKeyPress={(e)=>{e.charCode===13}}
//эта штука для того чтобы если нажимаешь на ентер, то таска добавлялась
//ЗАДАЧА-ЗАМЕНИТЬ НА ЧТО_ТО АКТУАЛЬНОЕ

//мы не должны писать функции в обработчиках событий, поэтому мы должны их выносить 


//четвертое занятие

export type FilterType='All'|'Active'|'Completed'

function App() {


let [tasks,setTasks] =useState([
    {
        task: 'js',
        id: v1(),
        description:'something',
        checked:true,
    },
    { task: 'sccs' ,
    id: v1(),
    description:'something',
    checked:true,
},
    { task: 'html',
    id: v1(),
    description:'something',
    checked: false, },
]) 

//если пакет вдруг не находится, подчеркивается красным, смотрим в пакидж джейсон - если там все ок, пишем хрень, возвращаем обратно, если все равно не видит - перезапуск редактора . 

//пишем функцию для изменения чекбокса. Мы находимся в апп , где хранятся данные, нам надо выпрыгнуть из функции, забраться в стор, и найти нужную таску. Когда мы используем что-то, что находится за пределами функции это называется замыкание
//ищем нужную таску при помощи файнд, а когда находим меняем на противоположное значение чекед через присвоение - пусть нам из вне приходит подсказка на что мы должны менять значение - и оборачиваем это присвоение в иф - проверку на андефайнд
//это проверка на псевдоистину и псведоложь - прочесть об этом больше
//затем нам надо сказать, что стор изменился , ведь мы поменяли значение -через сет таскс
//прокидываем, пишем ончейндж, кладем в него ончейндж хэндлер, который лежит в функции мап(создается для каждого чекбокса отдельно) и будет принимать событие е , тип подскажет редактор- мне не подсказал, вызываем хэнлер вот с такими параметрами  changeCheckBox(el.id, e.currentTarget.checked), потом видим что нихера не меняется, потому что сет таск не сработал - он не видит изменений в массиве- все объекты на месте, он не видит изменений внутри массива, поэтому пишем переменную копию, присваеваем ей массив, внутри таски со спред оператором
//почему-то очень странно работают таски в фильтре - при нажатии на чекбокс - удаление

//пишем функцию которая запрещает добавлять пустые таски - пишем в кнопке плюс - если валью.трим


function changeCheckBox(id:string, checked:boolean){

    let task=tasks.find(t=>t.id===id)
    if(task){
        task.checked=checked;
    }
//создаем видимость того, что здесь лежит что-то новое
    // let copy=[...tasks]
    // setTasks(copy)

    //сокращенный вариант
    setTasks([...tasks])
 
}

let [filter, setFilter]=useState<FilterType>('Active')

function addTask(newtext:string){
let newTask={
    task: newtext,
    id: v1(),
    description:'',
    checked:false,
}
setTasks([newTask,...tasks])
}

function changeFilter(value:FilterType){
    setFilter(value)
}

let tasksForFilter=tasks



if(filter==='Completed'){
    //без ретурна!!! присваеваем фильтр
     tasksForFilter=tasks.filter(
        el=>el.checked===true
    )
}


if(filter==='Active'){
    tasksForFilter=tasks.filter(
        el=>el.checked===false
    )
}



function deleteTask(id:string){

let filtredTasks=tasks.filter((el)=>{return el.id!==id})
setTasks(filtredTasks)

}
    return (
        <div className="App">
            <ToDoList title='watch' tasks={tasksForFilter} deleteTask={deleteTask}changeFilter={changeFilter}
            addTask={addTask} changeCheckBox={changeCheckBox}/>
        </div>
    );
}


export default App;
