import React, { useState, ChangeEvent, ChangeEventHandler } from 'react';
import { FilterType } from './App';


export type TasksType = {
    task: string,
    id: string,
    description: string,
    checked: boolean,
}

export type ToDoListType = {
    title: string,
    tasks: Array<TasksType>,
    deleteTask: (id: string) => any,
    changeFilter: (value: FilterType) => void
    addTask: (newtext: string) => any
    changeCheckBox:(id:string, checked:boolean)=>any
    err:str
}


export function ToDoList({ title, tasks, deleteTask, changeFilter, addTask, changeCheckBox }: ToDoListType) {


   
//даже те функции которые в обработчиках в методе мап нужно облегчать - пишем прямо в мап функцию, которая будет считываться при нажатии на кнопку
//задание- зарефакторить кселикс по этому методу
    let mapFunction = tasks.map((el: any) => {
        const onDeleteHandler=()=>{
            deleteTask(el.id)
        }
        const onChangeHandler=(e:any)=>{ 
            changeCheckBox(el.id, e.currentTarget.checked);
            
        }
        return <li key={el.id}><input type="checkbox" checked={el.checked} onChange={onChangeHandler}/> <span>{el.task}
            <button onClick={onDeleteHandler}>x</button></span></li>
    })

    let [value, setValue] = useState('')

    //мы не должны писать функции в обработчиках событий, поэтому мы должны их выносить 

    const onChabgeHandlerElement = (e: ChangeEvent<HTMLInputElement>) => { setValue(e.currentTarget.value) }

    const plusButton = () => {
        
        if(value.trim()!==''){
            { addTask(value.trim()); setValue(''); }
        }
    
       
    }

    const onAllClickHandler=()=>{
        changeFilter('All')
    }

    const onActiveClickHandler=()=>{
        changeFilter('Active')
    }

    const onCompletedClickHandler=()=>{
        changeFilter('Completed')
    }
  
   
    return (
        <div className="App">
            <div>
                <h3>{title}</h3>
                <div>
                    <input value={value} onChange={onChabgeHandlerElement
                    } className={erorr&&'erorr'}/>
                    <button onClick={plusButton}>+</button>
                </div>
                {erorr&& <div className="">erorr</div>}
                <ul>
                    {mapFunction}

                </ul>
                <div>
                    <button onClick={onAllClickHandler }>All</button>
                    <button onClick={onActiveClickHandler}>Active</button>
                    <button onClick={onCompletedClickHandler }>Completed</button>
                </div>
            </div>
        </div>
    );
}
