import React from 'react';


export type TasksType={
    task: string,
    id:string,
    description:string,
    checked:boolean,
}

export type ToDoListType = {
    title: string,
    tasks: Array<TasksType>,
    deleteTask:Function 
    filterTasks:Function 
}


export function ToDoList({ title, tasks,deleteTask ,filterTasks}: ToDoListType) {

    let mapFunction = tasks.map((el:any) => { return <li><input type="checkbox" checked={el.checked} /> <span>{el.task}
     <button onClick={()=>deleteTask(el.id)}>x</button></span></li> })
    return (
        <div className="App">
            <div>
                <h3>{title}</h3>
                <div>
                    <input />
                    <button>+</button>
                </div>
                <ul>
                    {mapFunction}
                   

                </ul>
                <div>
                    <button onClick={()=>filterTasks('All')}>All</button>
                    <button onClick={()=>filterTasks('Active')}>Active</button>
                    <button onClick={()=>filterTasks('Completed')}>Completed</button>
                </div>
            </div>
        </div>
    );
}
