import React from 'react';
import { v1 } from 'uuid';
import './App.css';


type ToDoListType = {
    title: string
    tasks: Array<Object>
}

let tasks1 = [
    {
        task: 'js',
        id: v1(),
        description:'something'
    },
    { task: 'sccs' ,
    id: v1(),
    description:'something'},
    { task: 'html',
    id: v1(),
    description:'something' },
]

let tasks2 = [
    { task: 'ssss' },
    { task: 'ssss' },
    { task: 'ssss' },
]

function App() {
    return (
        <div className="App">
            <ToDoList title='watch' tasks={tasks1} />
            <ToDoList title='learn' tasks={tasks2} />
        </div>
    );
}



function ToDoList({ title, tasks }: ToDoListType) {

    let mapFunction = tasks.map((el:any) => { return <li><input type="checkbox" checked={el.chec} /> <span>{el.task}</span></li> })
    return (
        <div className="App">
            <div>
                <h3>{title}</h3>
                <div>
                    <input />
                    <button>+</button>
                </div>
                <ul>
                    {mapFunction}

                </ul>
                <div>
                    <button>All</button>
                    <button>Active</button>
                    <button>Completed</button>
                </div>
            </div>
        </div>
    );
}


export default App;
